import React, { useState } from 'react'
import auth0 from '../lib/auth0'
import axios from 'axios'

const CreateStatus = () => {
    const [dados, setDados] = useState({
        status: 'bem',
        coords: {
            lat: null,
            long: null
        }
    })
    const getMyLocation = () => {
        if(navigator.geolocation){
            navigator.geolocation.getCurrentPosition(position => {
                //console.log(position.coords)
                setDados(old => {
                    return {
                        ...old,
                        coords: {
                            lat: position.coords.latitude,
                            long: position.coords.longitude
                        }
                    }
                })
            })
        }
    }
    const onStatusCnahge = evt => {
        const value = evt.target.value
        setDados(old => {
            return {
                ...old,
                status: value
            }
        })
    }
    const save = async() => {
        await axios.post('/api/save-status', dados)
    }
    return (
        <div>
            <h1>Selecione a opção que melhor define sua situação hoje:</h1>
            <label className='block' onClick={onStatusCnahge}>
                <input type='radio' name='status' value='bem'></input> Estou bem e sem sintomas
            </label>
            <label className='block' onClick={onStatusCnahge}>
                <input type='radio' name='status' value='gripe'></input> Estou com sintomas de gripe
            </label>
            <label className='block' onClick={onStatusCnahge}>
                <input type='radio' name='status' value='covid'></input> Estou com sintomas da covid
            </label>
            Sua posição atual: {JSON.stringify(dados)} <br/>
            <button onClick={getMyLocation} className='py-4 px-2 bg-pink-800 font-bold shadow-xl hover:shadow rounded w-1/4 text-center mx-auto text-white'>
                Get My Location
            </button> &nbsp;&nbsp;
            <button onClick={save} className='py-4 px-2 bg-pink-800 font-bold shadow-xl hover:shadow rounded w-1/4 text-center mx-auto text-white'>
                Save
            </button> &nbsp;&nbsp;            
        </div>
        
    )
}

export default CreateStatus

export async function getServerSideProps ({ req, res }) {
    const session = await auth0.getSession(req)    
    if(session){}
        return {
            props: {
                isAuth: true,
                user: session.user,                
            }
        }
        return {
            props: {
                isAuth: false,
                user: {}
            }
        }
    }
